var fMsg = msg.payload;
var batL = {payload: null};
var batV = {payload:null};
var batW = {payload:''};
var LOWV = 3.49;
var LVWARNING = 'Node N0SO battery voltage low: ';
//if (typeof fMsg.packet.decoded.payload.environmentMetrics !== 'undefined') {
    //envM = fMsg.packet.decoded.payload.environmentMetrics;
//}
if (typeof fMsg.packet.decoded.payload.deviceMetrics !== 'undefined') {
    //devM = fMsg.packet.decoded.payload.deviceMetrics;
    batV = {payload: fMsg.packet.decoded.payload.deviceMetrics.voltage};
    batL = { payload: fMsg.packet.decoded.payload.deviceMetrics.batteryLevel };
    if (batV.payload <= LOWV){
        let v=batV.payload;
        v = v.toFixed(2);
        batW = { payload: LVWARNING + v +' VDC.',
                 channel: 2 };

        // Order is battery guage/history, battery level, meshtastic alert, dashboard alert
        return [batV, batL, batW, batW];
    }
    else {
        return [batV, batL, null, batW];
    }
}

